mapper.select:
select id,username,job,department,hiredate,phone,status,domicile,sex,age,authority 
from user 

where username = #{username} and password = #{password}


where id = #{id}

mapper.update:

	update user set username = #{username}


	update user set password = #{password}

 where id = #{id}

UPDATE user SET 

	authority=#{authority} 
	where id=#{id} 


	username=#{username}
	,job=#{job}
	,department=#{department}
	,hiredate=#{hiredate}
	,phone=#{phone}
	,status=#{status}
	,domicile=#{domicile}
	,sex=#{sex}
	,age=#{age} 
	WHERE 
	id=#{id}
