mapper.select:
SELECT id,declareProject,declareNumber,declareFraction,declareScale,declareArrange,declareAssess,declareSurveying,declarePlan,contract,declareCompany,declareName,declarePhone,finishTime,quality,bid,responsibilityName,responsibilityPhone FROM declare_project
WHERE id=#{id}
WHERE declareProject LIKE CONCAT("%",#{declareProject},"%")
